    private void resetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_resetActionPerformed
        // TODO add your handling code here:
        pepino.setSelected(false);
        cebola.setSelected(false);
        coentros.setSelected(false);
        salada.clearSelection();
        vinagre.setSelectedIndex(0);
        botoes.clearSelection();
        quantidade.setValue(0);
        tamanho.setValue(2);
        msalada.setText("");
    }//GEN-LAST:event_resetActionPerformed

    private void lojaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_lojaActionPerformed
        // TODO add your handling code here:
        if (loja.isSelected()) {
            loja.setText("Loja Fechada");
            pepino.setEnabled(false);
            cebola.setEnabled(false);
            coentros.setEnabled(false);
            salada.setEnabled(false);
            vinagre.setEnabled(false);
            natural.setEnabled(false);
            fria.setEnabled(false);
            quantidade.setEnabled(false);
            tamanho.setEnabled(false);
            encomendar.setEnabled(false);
            reset.setEnabled(false);
        } else {
            loja.setText("Loja Aberta");
            pepino.setEnabled(true);
            cebola.setEnabled(true);
            coentros.setEnabled(true);
            salada.setEnabled(true);
            vinagre.setEnabled(true);
            natural.setEnabled(true);
            fria.setEnabled(true);
            quantidade.setEnabled(true);
            tamanho.setEnabled(true);
            encomendar.setEnabled(true);
            reset.setEnabled(true);
        }
    }//GEN-LAST:event_lojaActionPerformed

