    private void encomendarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_encomendarActionPerformed
        // TODO add your handling code here:        
        msalada.setText("A sua salada será composta por: ");
        if (pepino.isSelected()) {
            msalada.setText(msalada.getText() + "\n" + "- Pepino.");
        }
        if (cebola.isSelected()) {
            msalada.setText(msalada.getText() + "\n" + "- Cebola.");
        }
        if (coentros.isSelected()) {
            msalada.setText(msalada.getText() + "\n" + "- Coentros.");
        }
        msalada.setText(msalada.getText() + "\n");
        msalada.append("Também leva " + salada.getSelectedValuesList() + ".");

        msalada.setText(msalada.getText() + "\n" + "\n" + "O vinagre será: ");
        if (vinagre.getSelectedItem().toString() != null) {
            msalada.setText(msalada.getText() + "\n" + "- " + vinagre.getItemAt(vinagre.getSelectedIndex()) + ".");
        }

        msalada.setText(msalada.getText() + "\n" + "\n" + "A salada encontrar-se-á: ");
        if (natural.isSelected()) {
            msalada.setText(msalada.getText() + "\n" + "- Natural.");
        }
        if (fria.isSelected()) {
            msalada.setText(msalada.getText() + "\n" + "- Fria.");
        }

        msalada.setText(msalada.getText() + "\n" + "\n" + "A quantidade da sua encomenda será de: " + "\n" + "- " + (quantidade.getValue()) + " saladas.");

        msalada.setText(msalada.getText() + "\n" + "\n" + "O tamanho da sua salada será: " + "\n" + "- " + (tamanho.getValue()) + ".");


    }//GEN-LAST:event_encomendarActionPerformed

